import React from 'react'
import {View,Text} from 'react-native'
import { StyleSheet, TouchableOpacity } from 'react-native-web'
let main = "https://firebasestorage.googleapis.com/v0/b/sparta-image.appspot.com/o/lecture%2FaboutImage.png?alt=media&token=13e1c4f6-b802-4975-9773-e305fc7475c4"

export default function AboutPage(){
  return (
    <View style={styles.container}>
        <Text style={styles.title}>HI! 스파르타코딩 앱개발 반에 오신 것을 환영합니다!</Text>
        <View style={styles.card}>
            <Image style={styles.cardImg} source={{uri:main}}
            />
            <Text style={styles.desc1}>많은 내용을 간결하게 담아내려 노력했습니다!</Text>
            <Text style={styles.desc2}>꼭 완주 하셔서 곡 여러분 것으로 만들어 가시길 바랍니다</Text>
            <TouchableOpacity style={styles.btn}>
                <Text style={styles.btnTxt}>@chosen.1_errSum</Text>
            </TouchableOpacity>
        </View>
    </View>
      )
}

const styles = StyleSheet.create({
    container : {
        backgroundColor: 'blue'
    },
    title : {
        width: '95%',
        height: 100, 

        // 폰트 크기
        fontSize: 20,

        // 폰트 굴기
        fontWeight: '700',

        // 공간 이격
        margin: 40
    },
    card : {
        width: '95%',
        height: 400,
    }
})
